window.margin = 10;

function layout(bodyMargin = 0) {
	var css = `
		input:focus {
			outline-width: 0;
		}
		body {
			visibility: initial;
			position: relative;
			margin: ${bodyMargin}px;
			width: calc(100% - ${bodyMargin * 2}px);
			height: calc(100% - ${bodyMargin * 2}px);
		}
		#ExplainGitZen-Container .playground-container {
			top: 0;
			left: 0;
			bottom: 0;
			right: 0;
			margin-top: 0;
		}
		#ExplainGitZen-Container .svg-container.remote-container {
			top: 215px;
			right: 15px;
            border: 0;
		}
		.control-box {
			z-index: 1000;
			height: 200px;
			border-color: white;
			width: 100%;
			border: 0;
		}
		.control-box .log {
			top: 0;
			bottom: 47px;
			border: 0;
			border-bottom: 1px solid gray;
		}
		.control-box .log .info:first-child {
			display: none;
		}
		.control-box .log * {
			font-size: 20pt !important;
			line-height: 20pt !important;
			background-position: 0px 5px !important;
		}
		.control-box .log .info > span {
			display: block;
		}
		.control-box .log .error {
			display: none;
		}
		.control-box select {
			display: none;
		}
		.control-box input[type="text"] {
			font-size: 30pt;
			width: calc(100% - 17px);
		}
		#ExplainGitZen-Container .svg-container {
			left: 0;
			border: 0;
			top: 200px;
		}
		#sergiocabral {
			position: fixed;
			top: ${bodyMargin + 5}px;
			right: ${bodyMargin + 20}px;
			cursor: pointer;
			z-index: 2000;
		}
		#version {
			position: fixed;
			left: 10px;
			bottom: 10px;
			z-index: 100;
			background-color: #DFDFDF;
			padding: 3px 6px;
			font-size: 10px;
			box-shadow: 0px 0px 6px 3px rgba(0,0,0,0.3);
			border-radius: 100%;
			color: gray;			
		}
	`;

	var style = document.getElementById("layout") || document.createElement("STYLE");
	style.id = "layout";
	style.innerHTML = css;
	document.body.append(style);

	document.body.style.display = "";
	document.title = "Git Playground";
}

function hideAllWhenLoading() {
	var style = document.getElementById("hideAllWhenLoading") || document.createElement("STYLE");
	style.id = "hideAllWhenLoading";
	style.innerHTML = "body { visibility: hidden; }";
	document.body.append(style);
}
hideAllWhenLoading();

function addLinkButtonToProject() {
  var img = document.createElement("IMG");
  img.onclick = function() { window.open("/ebook"); }
  img.id = "sergiocabral";
  img.src = "data:image/png;base64,"
  document.body.append(img);
}

async function addVersionStamp() {
	var localExecution = location.href.indexOf('file://') === 0;
	var version = "v0.0.0";
	if (!localExecution) {
		var response = await fetch(location.pathname + "/VERSION");
		var text = await response.text();
		version = text.trim().replace("#BUILD#", "x");
	}
	var el = document.createElement("div");
	el.id = "version";
	el.innerHTML = version;
	el.title = "Version of this web app.";
	document.body.append(el);
}

function whenErrorReloadPage() { 
	if (document.querySelector('.control-box')) return;
	return location.href = location.origin + location.pathname + location.search;
}

function selectGitMode(mode = "free-remote") {	
	var select = document.querySelector('.control-box select');
	select.value = mode;
	select.dispatchEvent(new Event('change'));
}

window.confirm = function() {
	console.log("confirm", arguments);
	return true;
}

document.onreadystatechange = function () {
	if (document.readyState != "interactive") return;
	var load = function () {
		if (whenErrorReloadPage()) return;
		selectGitMode();
		layout(window.margin);
		addLinkButtonToProject();
		addVersionStamp();
	};
	setTimeout(load, 1000);
}
